import{_ as t,o as e,c as n,e as l}from"./app-d85a60fe.js";const d={},r=l('<h1 id="enterprise-architecture" tabindex="-1"><a class="header-anchor" href="#enterprise-architecture" aria-hidden="true">#</a> Enterprise Architecture</h1><p>5W1H Zachman 1987</p><table><thead><tr><th style="text-align:center;"></th><th style="text-align:center;">数据（what）</th><th style="text-align:center;">功能（how）</th><th style="text-align:center;">网络（where）</th><th style="text-align:center;">角色（who）</th><th style="text-align:center;">时间（when）</th><th style="text-align:center;">动机（why）</th></tr></thead><tbody><tr><td style="text-align:center;">目标范围</td><td style="text-align:center;">列出对业务至关重要的元素</td><td style="text-align:center;">列出业务执行的流程</td><td style="text-align:center;">列出与业务运营有关的地域分布要求</td><td style="text-align:center;">列出对业务重要的组织部门</td><td style="text-align:center;">列出对业务重要的事件及事件周期</td><td style="text-align:center;">列出企业目标、战略</td></tr><tr><td style="text-align:center;">业务模型</td><td style="text-align:center;">实体关系图（包括 M:M 关系、N-ary 关系、归因关系</td><td style="text-align:center;">业务流程模型（物理数据流程图）</td><td style="text-align:center;">物流网络（节点和链接）</td><td style="text-align:center;">基于角色的层次组织图，包括相关技能规定、安全保障问题</td><td style="text-align:center;">业务主进度表</td><td style="text-align:center;">业务计划</td></tr><tr><td style="text-align:center;">信息系统模型</td><td style="text-align:center;">数据模型（聚合体、完全规格化）</td><td style="text-align:center;">关键数据流程图、应用架构</td><td style="text-align:center;">分布系统架构</td><td style="text-align:center;">人机界面架构（角色、数据、入口）</td><td style="text-align:center;">相依关系图、数据实体生命历程（流程结构）</td><td style="text-align:center;">业务标准模型</td></tr><tr><td style="text-align:center;">技术模型</td><td style="text-align:center;">数据架构（数据库中的表格列表及属性）、遗产数据图</td><td style="text-align:center;">系统设计：结构图、伪代码</td><td style="text-align:center;">系统架构（硬件、软件类型）</td><td style="text-align:center;">用户界面（系统如何工作）、安全设计</td><td style="text-align:center;">“控制流“图（控制结构）</td><td style="text-align:center;">业务标准设计</td></tr><tr><td style="text-align:center;">详细展现</td><td style="text-align:center;">数据设计（反向规格化）、物理存储器设计</td><td style="text-align:center;">详细程序设计</td><td style="text-align:center;">网络架构</td><td style="text-align:center;">屏显、安全机构（不同种类数据源的开放设定）</td><td style="text-align:center;">时间、周期定义</td><td style="text-align:center;">程序逻辑的角色说明</td></tr><tr><td style="text-align:center;">功能系统</td><td style="text-align:center;">转化后的数据</td><td style="text-align:center;">可执行程序</td><td style="text-align:center;">通信设备</td><td style="text-align:center;">受训的人员</td><td style="text-align:center;">企业业务</td><td style="text-align:center;">强制标准</td></tr></tbody></table>',3),a=[r];function c(s,i){return e(),n("div",null,a)}const y=t(d,[["render",c],["__file","EA-5W1H.html.vue"]]);export{y as default};
