import{_ as a,o as e,c as r,e as i}from"./app-d85a60fe.js";const h={},d=i('<h1 id="ul-info" tabindex="-1"><a class="header-anchor" href="#ul-info" aria-hidden="true">#</a> UL INFO</h1><h2 id="角色" tabindex="-1"><a class="header-anchor" href="#角色" aria-hidden="true">#</a> 角色</h2><h3 id="kcm-kc客户经理" tabindex="-1"><a class="header-anchor" href="#kcm-kc客户经理" aria-hidden="true">#</a> KCM-KC客户经理</h3><h3 id="uss-销售主任-负责da" tabindex="-1"><a class="header-anchor" href="#uss-销售主任-负责da" aria-hidden="true">#</a> USS-销售主任-负责DA</h3><h3 id="sr-sales-representative-销售代表" tabindex="-1"><a class="header-anchor" href="#sr-sales-representative-销售代表" aria-hidden="true">#</a> SR-Sales Representative-销售代表</h3><h3 id="ss-sales-supervisor-销售主管" tabindex="-1"><a class="header-anchor" href="#ss-sales-supervisor-销售主管" aria-hidden="true">#</a> SS-Sales Supervisor-销售主管</h3><h3 id="om-运营经理" tabindex="-1"><a class="header-anchor" href="#om-运营经理" aria-hidden="true">#</a> OM-运营经理</h3><h3 id="dsr-经销商销售代表" tabindex="-1"><a class="header-anchor" href="#dsr-经销商销售代表" aria-hidden="true">#</a> DSR-经销商销售代表</h3><h3 id="dss-经销商销售主管" tabindex="-1"><a class="header-anchor" href="#dss-经销商销售主管" aria-hidden="true">#</a> DSS-经销商销售主管</h3><h3 id="asm-地区销售经理" tabindex="-1"><a class="header-anchor" href="#asm-地区销售经理" aria-hidden="true">#</a> ASM-地区销售经理</h3><h3 id="rsd-region-sales-director-大区销售总监" tabindex="-1"><a class="header-anchor" href="#rsd-region-sales-director-大区销售总监" aria-hidden="true">#</a> RSD-Region Sales Director-大区销售总监</h3><h3 id="de-distributor-executive-经销商管理主管" tabindex="-1"><a class="header-anchor" href="#de-distributor-executive-经销商管理主管" aria-hidden="true">#</a> DE-Distributor Executive-经销商管理主管</h3><h3 id="rka-通路重点客户" tabindex="-1"><a class="header-anchor" href="#rka-通路重点客户" aria-hidden="true">#</a> RKA-通路重点客户</h3><h3 id="pmg-promotion-girls-促销员" tabindex="-1"><a class="header-anchor" href="#pmg-promotion-girls-促销员" aria-hidden="true">#</a> PMG- Promotion Girls-促销员</h3><h3 id="pmr-促销代表" tabindex="-1"><a class="header-anchor" href="#pmr-促销代表" aria-hidden="true">#</a> PMR-促销代表</h3><h3 id="dmd-distributor-merchandiser-经销商理货员" tabindex="-1"><a class="header-anchor" href="#dmd-distributor-merchandiser-经销商理货员" aria-hidden="true">#</a> DMD-Distributor Merchandiser-经销商理货员</h3><h3 id="rmd-running-merchandiser-流动理货员-kc" tabindex="-1"><a class="header-anchor" href="#rmd-running-merchandiser-流动理货员-kc" aria-hidden="true">#</a> RMD-Running Merchandiser-流动理货员（KC）</h3><h3 id="smd-store-merchandiser-驻店理货员-长促" tabindex="-1"><a class="header-anchor" href="#smd-store-merchandiser-驻店理货员-长促" aria-hidden="true">#</a> SMD-Store Merchandiser-驻店理货员（长促）</h3><h3 id="drp-distribution-resource-planner-分销资源计划员" tabindex="-1"><a class="header-anchor" href="#drp-distribution-resource-planner-分销资源计划员" aria-hidden="true">#</a> DRP-Distribution Resource Planner-分销资源计划员</h3><h3 id="lkc-local-key-customer-地区主要客户" tabindex="-1"><a class="header-anchor" href="#lkc-local-key-customer-地区主要客户" aria-hidden="true">#</a> LKC-Local Key Customer-地区主要客户</h3><h2 id="名词" tabindex="-1"><a class="header-anchor" href="#名词" aria-hidden="true">#</a> 名词</h2><h3 id="tts-trading-terms-spending-贸易条款" tabindex="-1"><a class="header-anchor" href="#tts-trading-terms-spending-贸易条款" aria-hidden="true">#</a> TTS-Trading Terms Spending-贸易条款</h3><h3 id="pwp-promotion-working-plan-促销计划" tabindex="-1"><a class="header-anchor" href="#pwp-promotion-working-plan-促销计划" aria-hidden="true">#</a> PWP-Promotion Working Plan-促销计划</h3><h3 id="pdp-permanent-delivery-plan-固定送货计划-物流" tabindex="-1"><a class="header-anchor" href="#pdp-permanent-delivery-plan-固定送货计划-物流" aria-hidden="true">#</a> PDP-Permanent Delivery Plan-固定送货计划（物流）</h3><h3 id="eco-有效覆盖率" tabindex="-1"><a class="header-anchor" href="#eco-有效覆盖率" aria-hidden="true">#</a> ECO-有效覆盖率</h3><h3 id="bp-拜访成功率" tabindex="-1"><a class="header-anchor" href="#bp-拜访成功率" aria-hidden="true">#</a> BP-拜访成功率</h3><h3 id="llpc-每张订单品种数" tabindex="-1"><a class="header-anchor" href="#llpc-每张订单品种数" aria-hidden="true">#</a> LLPC-每张订单品种数</h3><h3 id="sos-货架占比" tabindex="-1"><a class="header-anchor" href="#sos-货架占比" aria-hidden="true">#</a> SOS-货架占比</h3><h3 id="som-市场占有率" tabindex="-1"><a class="header-anchor" href="#som-市场占有率" aria-hidden="true">#</a> SOM-市场占有率</h3><h3 id="wtd-weight-distribution-权重分销" tabindex="-1"><a class="header-anchor" href="#wtd-weight-distribution-权重分销" aria-hidden="true">#</a> WTD-Weight Distribution-权重分销</h3><h3 id="sppd-sales-per-point-distribution-单店产能" tabindex="-1"><a class="header-anchor" href="#sppd-sales-per-point-distribution-单店产能" aria-hidden="true">#</a> SPPD-Sales per Point Distribution-单店产能</h3><h3 id="sih-share-in-handle-单店市场份额" tabindex="-1"><a class="header-anchor" href="#sih-share-in-handle-单店市场份额" aria-hidden="true">#</a> SIH-Share in Handle-单店市场份额</h3><h3 id="rs-road-show-路演" tabindex="-1"><a class="header-anchor" href="#rs-road-show-路演" aria-hidden="true">#</a> RS-Road Show-路演</h3><h3 id="rtm-route-to-market-深度分销" tabindex="-1"><a class="header-anchor" href="#rtm-route-to-market-深度分销" aria-hidden="true">#</a> RTM-Route to Market-深度分销</h3><h3 id="osa-on-shelf-availability-货架有货率" tabindex="-1"><a class="header-anchor" href="#osa-on-shelf-availability-货架有货率" aria-hidden="true">#</a> OSA-On-Shelf Availability-货架有货率</h3><p>货架有货率每提高3%，就会带动产品销售提高1%</p><h3 id="dtr-distributor-retailer-经销商门店" tabindex="-1"><a class="header-anchor" href="#dtr-distributor-retailer-经销商门店" aria-hidden="true">#</a> DTR-Distributor Retailer-经销商门店</h3><h3 id="pop-point-of-purchase-卖点广告" tabindex="-1"><a class="header-anchor" href="#pop-point-of-purchase-卖点广告" aria-hidden="true">#</a> POP-Point of Purchase-卖点广告</h3><h2 id="销售架构及职责" tabindex="-1"><a class="header-anchor" href="#销售架构及职责" aria-hidden="true">#</a> 销售架构及职责</h2><h3 id="销售总监-经理" tabindex="-1"><a class="header-anchor" href="#销售总监-经理" aria-hidden="true">#</a> 销售总监/经理</h3><h3 id="督察" tabindex="-1"><a class="header-anchor" href="#督察" aria-hidden="true">#</a> 督察</h3><p>检查陈列面和SKU数量是否达标 POP张贴、价格牌挂放 检查结果（拜访表） 整理汇总每日市场检查结果 协助评定DSR工资</p><h3 id="文员" tabindex="-1"><a class="header-anchor" href="#文员" aria-hidden="true">#</a> 文员</h3><p>建立DSR人员个人档案 统计月促销费用跟进 协助制作工资报表 接听批发商订单</p><h3 id="ss" tabindex="-1"><a class="header-anchor" href="#ss" aria-hidden="true">#</a> SS</h3><h3 id="sr" tabindex="-1"><a class="header-anchor" href="#sr" aria-hidden="true">#</a> SR</h3><h3 id="dss" tabindex="-1"><a class="header-anchor" href="#dss" aria-hidden="true">#</a> DSS</h3><ul><li><p>制定标准</p><p>产品陈列、可视化要求和拜访路线设计</p></li><li><p>日常管理</p><p>出勤、拜访频率执行考核、工作表管理</p></li><li><p>数据维护</p><p>每日拜访家数、可视性执行表现</p></li><li><p>主持会议</p><p>周会工作总结及工作安排</p></li><li><p>工资考核</p><p>协助进行DSR工资考核</p></li></ul><h3 id="dsr" tabindex="-1"><a class="header-anchor" href="#dsr" aria-hidden="true">#</a> DSR</h3><p>店内执行</p><ul><li><p>产品陈列</p></li><li><p>售点可视</p></li><li><p>信息回馈</p><p>促销政策通知及反馈信息</p></li><li><p>拜访记录</p></li></ul><h2 id="人员配置" tabindex="-1"><a class="header-anchor" href="#人员配置" aria-hidden="true">#</a> 人员配置</h2><h3 id="每日拜访门店数" tabindex="-1"><a class="header-anchor" href="#每日拜访门店数" aria-hidden="true">#</a> 每日拜访门店数</h3><p>每日拜访门店数 = （工作时间 - 路途时间） / 单点拜访时间 注：路途时间 = 乘车时间 + 在途时间</p><h3 id="dsr人员数" tabindex="-1"><a class="header-anchor" href="#dsr人员数" aria-hidden="true">#</a> DSR人员数</h3><p>DSR人数 = （门店数 * 每月拜访率） / （每日拜访门店数 * 每月工作天数）</p><h3 id="减低人员流失率" tabindex="-1"><a class="header-anchor" href="#减低人员流失率" aria-hidden="true">#</a> 减低人员流失率</h3><p>1）从招募开始留住员工 招聘态度、招聘技巧、建立人事档案 2）合理的薪酬表达对员工的认可 提供有竞争力的薪酬、人性化的激励制度 3）培训投资员工 基础性培训、提升自我培训 4）提供员工发展空间 晋升制度、轮岗&amp;转岗</p><h2 id="固定拜访计划-pjp" tabindex="-1"><a class="header-anchor" href="#固定拜访计划-pjp" aria-hidden="true">#</a> 固定拜访计划（PJP）</h2><h3 id="含义" tabindex="-1"><a class="header-anchor" href="#含义" aria-hidden="true">#</a> 含义</h3><p>是DSR每日拜访客户所必须依照的路线；依据科学的方法经规划而制定，线路应该体现客户拜访的计划性和高效性</p><h3 id="目的" tabindex="-1"><a class="header-anchor" href="#目的" aria-hidden="true">#</a> 目的</h3><p>有计划有目的的客户拜访，使拜访高效率化，低成本化 有规律的固定拜访让客户感受到公司的售后服务 长期的固定拜访可以培养出客户的忠诚度</p><h3 id="制定和执行要求" tabindex="-1"><a class="header-anchor" href="#制定和执行要求" aria-hidden="true">#</a> 制定和执行要求</h3><p>拜访路线制定后，DSR必须严格执行，不能改变路线，特殊情况，须经DSS同意，违反者将被书面警告，两次书面警告，可以无条件解除合同</p><h3 id="规划固定拜访的步骤" tabindex="-1"><a class="header-anchor" href="#规划固定拜访的步骤" aria-hidden="true">#</a> 规划固定拜访的步骤</h3><p>确定所要覆盖的区域 确定区域内的目标零售网点 确定目标网点的拜访频率 将零售网点标记在地图上，进行路线规划 调整路线，提高有限工作时间内成本最小化，效率最大化 执行中的完善</p><h3 id="使用固定拜访计划" tabindex="-1"><a class="header-anchor" href="#使用固定拜访计划" aria-hidden="true">#</a> 使用固定拜访计划</h3><p>1）拜访前 确认当天的拜访路线 准备销售工具 2）拜访中 严格按照步骤执行工作流程中要求的内容 3）拜访后 记录当日工作情况，填写日工作表格</p><h3 id="出勤监管" tabindex="-1"><a class="header-anchor" href="#出勤监管" aria-hidden="true">#</a> 出勤监管</h3><p>1）拜访前 当日拜访路线地图 拜访工具 拜访记录表 2）拜访中 抽查是否按照当日线路拜访 现场观察和指导工作步骤 3）拜访后 检查当日报表填写是否符合要求 及时解决当日工作问题</p><h2 id="销售员的一天" tabindex="-1"><a class="header-anchor" href="#销售员的一天" aria-hidden="true">#</a> 销售员的一天</h2><h3 id="准备" tabindex="-1"><a class="header-anchor" href="#准备" aria-hidden="true">#</a> 准备</h3><ul><li><p>查阅上次拜访的记录/问题/机会</p></li><li><p>确定当天的拜访客户、目标、行程</p><p>目标：对每一个客户设定目标 销量目标 分销、陈列标准、收款、促销活动 开发新网点 目标必须明确 拜访目标应与上次拜访结果有关联性</p></li><li><p>预计可能的困难及解决办法</p><p>顾客可能的异议 你的说服理由 辅助材料、证明数据 可用“如果..那么”这样的设问自我提醒</p></li><li><p>检查销售工具是否齐全</p></li></ul><h3 id="市场拜访" tabindex="-1"><a class="header-anchor" href="#市场拜访" aria-hidden="true">#</a> 市场拜访</h3><ul><li><p>销量</p></li><li><p>固定拜访区域内所有网点</p></li><li><p>与客户建立良好的关系</p></li><li><p>管好每家门店</p><p>分销、陈列、价格、库存、助销、促销</p></li></ul><h3 id="总结" tabindex="-1"><a class="header-anchor" href="#总结" aria-hidden="true">#</a> 总结</h3><ul><li><p>完成所有的记录和报告</p><p>销售订单 日销售报告/日活动报告 记录客户情况 文件存档系统 协调库存和收款</p></li><li><p>回顾当前业绩与目标的对比</p><p>我达到了今天的目标？ 我今天有哪些成功？为什么？ 有哪些问题，我如何改进？ 我现在离我的本周目标和本月目标有多远？ 拜访效率，拜访时间利用？</p></li><li><p>为第二天做计划</p></li><li><p>为第二天准备销售工具</p></li></ul><h3 id="_6个销售工具" tabindex="-1"><a class="header-anchor" href="#_6个销售工具" aria-hidden="true">#</a> 6个销售工具</h3><ul><li>固定拜访路线行程表PJP</li><li>随身卡</li><li>销售报表</li><li>商品陈列材料</li><li>销售说明手册</li><li>其他</li></ul><h3 id="_6个基本拜访步骤" tabindex="-1"><a class="header-anchor" href="#_6个基本拜访步骤" aria-hidden="true">#</a> 6个基本拜访步骤</h3><ul><li><p>备</p><p>1）查阅上次拜访的结果/记录 寻找销售机会，确认尚待解决的问题 2）查阅本次拜访目标 预测困难、可能出现的问题 3）快速检查用具和销售工具/数据 4）向店主和店面工作人员问候，增进客情关系</p></li><li><p>查（店面检查）</p><p>目的：寻找销售机会、计划如何谈判、确认辅助销售工具</p><p>1）铺货，根据铺货标准进行分销/新品表现 2）陈列，根据陈列要求，检查/记录货架 /二级陈列问题，检查“先进先出”、近期货/残损货 3）价格，记录价格异常变动的品项 4）库存，记录缺/短货/库存过多 5）助销，使用POSM/货架陈列卡/自制助销工具 6）促销，记录促销执行情况 7）竞争，记录主要竞争对手表现/信息</p><ul><li><p>门店信息</p><p>1）所有相关人员的姓名、职务及职责 2）定货流程 3）门店的权限 4）是否有权：调整价格、调整陈列、决定促销、结款等 5）门店陈列标准：按品类、品牌、用途等 6）我司产品销售情况 7）竞争对手信息：分销、陈列、价格、促销等 8）门店潜在的增长机会</p></li></ul></li><li><p>卖（店方商谈）</p><p>1）根据查店结果，补货/获取新订单 2）根据查店结果，现场沟通，改进陈列排面 3）谈话包括：产品进场、补货订单、库存调整、价格调整、陈列改进、促销活动及执行等</p></li><li><p>收（账款管理）</p><p>1）确认是否存在超期账款，信用额度问题 2）确认是否存在账单/发票/改票问题 3）记录相关事项解决或协助反馈公司</p></li><li><p>翻（改善排面）</p><p>1）动手调整排面/改善/维护排面 2）确保价签信息准确无误/维护/张贴POSM POP 3）与店方人员沟通，请求协助维护</p></li><li><p>写（记录核实）</p><p>1）填写日报表 2）整理拜访记录、信息 3）安排下次拜访日期与目标</p></li></ul><h2 id="swot" tabindex="-1"><a class="header-anchor" href="#swot" aria-hidden="true">#</a> SWOT</h2><h3 id="优势" tabindex="-1"><a class="header-anchor" href="#优势" aria-hidden="true">#</a> 优势</h3><p>1）丰富的经验和技术，UL是最早进入中国市场的日化行业之一，对中国市场有一定的了解 2）品牌知名度高，世界上最大的日化企业之一，有着悠久的历史和知名度 3）良好的企业形象，不断致力于公益事业，有良好的公众形象 4）优秀的人力资源，UL在上海设立了全球研发中心，能够吸纳更多优秀人才，研发更好的产品</p><h3 id="劣势" tabindex="-1"><a class="header-anchor" href="#劣势" aria-hidden="true">#</a> 劣势</h3><p>1）管理模式松散，内部管理上存在一定问题 2）经营成本高，内部还存在资源浪费现象，企业的经营成本相对较高 3）品牌认知不清楚，一系列不知名、低卷入的品牌导致品牌层次过多</p><h3 id="机会" tabindex="-1"><a class="header-anchor" href="#机会" aria-hidden="true">#</a> 机会</h3><p>金融危机给UL一个创出更高业绩的机会，在经济不景气的时期，消费者倾向于选择性价比高的产品，而不是价钱低的产品。而UL一致致力于通过创新给消费者带来性价比最高的消费体验</p><h3 id="威胁" tabindex="-1"><a class="header-anchor" href="#威胁" aria-hidden="true">#</a> 威胁</h3><p>1）政治因素，国家出台一系列政策以鼓励帮助本土企业的发展，相对来说本土企业的发展对UL构成了威胁 2）强有力的竞争者，宝洁和欧莱雅的强势竞争，宝洁已牢牢占据市场份额的首位，而欧莱雅正在不断超越UL 3) 本土企业的发展，目前在织物、家居护理产品中，本土企业已经处于市场主导地位，经过一段时间的历练，本土企业将不断发展壮大，对UL构成强有力的威胁</p><h2 id="分销渠道" tabindex="-1"><a class="header-anchor" href="#分销渠道" aria-hidden="true">#</a> 分销渠道</h2><h3 id="渠道层次" tabindex="-1"><a class="header-anchor" href="#渠道层次" aria-hidden="true">#</a> 渠道层次</h3><ul><li><p>渠道长度</p><p>渠道长度受市场、产品、消费者的购买行为等因素影响 选用较长的分销渠道，各个省份设置区域代理商，并往下延伸出多级的批发商和零售商。 为了更好地为城市的一些大型卖场及时提供货物，打破了原有的渠道格局，将大卖场、超市、量贩店等现代通路独立出来，作为直供客户和主要客户，UL直接负责供货。这些是短渠道或者说是一层渠道。</p></li><li><p>渠道宽度</p><p>密集分销方式为主。在某一个地区尽可能的通过许多负责人的、适当的批发商、零售商推销其产品。</p></li><li><p>渠道广度</p><p>选择几条渠道来分销自己的产品极为重要。采用多渠道分销组合的混合型组合方式。综合运用集中型和选择性的组合方式。</p></li></ul><h3 id="渠道模式" tabindex="-1"><a class="header-anchor" href="#渠道模式" aria-hidden="true">#</a> 渠道模式</h3><ul><li><p>传统分销渠道-松散型渠道关系</p><p>分销商和零售商的独立性比较强。 分销商可以根据市场的需求选择自己推广谁的产品。不利于品牌推广，也不利于渠道成员之间的紧密联系。</p></li><li><p>垂直的分销渠道模式</p><p>建立公司型渠道关系。建立自己的销售分公司，通过制度管理，保持公司行动的一致性，有利于品牌的统一化，减少流通环节，节省费用。</p></li><li><p>共生型的渠道关系</p><p>与百度营销平台合作，实现优势互补，分享市场，规避风险，互惠互利，最终实现共赢。</p></li></ul><h3 id="渠道成员管理" tabindex="-1"><a class="header-anchor" href="#渠道成员管理" aria-hidden="true">#</a> 渠道成员管理</h3><ul><li><p>激励渠道成员</p><p>产品从制造商到用户的整个过程需要催化剂，有效的激励措施就是这样的催化剂。 目的是希望中间商多提货、早回款、希望渠道能抵御各种风险，实现公司的销售目标，提高公司销售绩效。</p><ul><li><p>直接激励</p><ul><li><p>年折扣</p></li><li><p>季度奖励</p><p>每季度结束后两个月内，按一定的进货比例以产品的形式给予。 每季度末派销售主管对中间商的业务代表培训指导，帮忙落实下一季度销售量及实施办法。</p></li><li><p>年度奖励</p></li><li><p>专卖奖励</p></li><li><p>下年度支持奖励</p><p>对当年完成销量目标，继续和制造商合作，且已续签销售合同的中间商的次年销售活动的支持。 每个季度会对经销商考评：实际销售量、区域销售市场的占有率、是否维护UL产品销售市场及销售价格的稳定、是否执行厂家的销售政策及策略。</p></li></ul></li><li><p>间接激励</p><ul><li><p>提供终端管理</p><p>通过定期拜访、帮助零售商整理货架、设计商品陈列形式。 主要做法：指定更完善的覆盖区域划分、向分销商派驻场代表协助销售、协助培训分销商和售货人员、招聘专职市场覆盖人员并负责对其工资奖金的发放、为分销商提供覆盖服务费、确立14天回款返利3%的回款激励系统、协助分销商提高物流管理水平并推行数字化管理。</p></li><li><p>提供店面建设</p><p>提供各种终端支持、奖励及相关管理政策。</p></li><li><p>吸收小代理商单店加入</p></li><li><p>提供产品支持和技术支持</p></li></ul></li><li><p>关系激励</p><ul><li><p>“实”和“虚”的关系</p><p>物质奖励、精神奖励</p></li><li><p>“正”和“负”的关系</p><p>正激励负激励，奖罚分明、奖惩适度、公平公正、一视同仁。</p></li><li><p>“重”和“轻”的关系</p><p>重点激励和一般激励。</p></li></ul></li></ul></li></ul><h2 id="名词2" tabindex="-1"><a class="header-anchor" href="#名词2" aria-hidden="true">#</a> 名词2</h2><h3 id="sell-in-卖进" tabindex="-1"><a class="header-anchor" href="#sell-in-卖进" aria-hidden="true">#</a> sell-in 卖进</h3><p>是指产品从厂家销售到经销商、代理商（渠道）的过程，是产品进入渠道流转的开始，即批发、整批出售</p><h3 id="sell-through-销售流通" tabindex="-1"><a class="header-anchor" href="#sell-through-销售流通" aria-hidden="true">#</a> sell-through 销售流通</h3><p>是指渠道营销中，产品从总代理销售给下级代理直到终端零售商的过程。 这个过程中，产品走完渠道的整个流程</p><h3 id="sell-out-卖出" tabindex="-1"><a class="header-anchor" href="#sell-out-卖出" aria-hidden="true">#</a> sell-out 卖出</h3><p>是产品从终端零售商出售给最终用户的过程</p><h2 id="店内" tabindex="-1"><a class="header-anchor" href="#店内" aria-hidden="true">#</a> 店内</h2><h3 id="zone" tabindex="-1"><a class="header-anchor" href="#zone" aria-hidden="true">#</a> Zone</h3><ul><li>Hunter Zone-刺激（购买）区域</li><li>Discovery Zone-发现（特别产品）区域</li><li>Orientation Zone-引导（消费者）区域</li><li>Browsing Zone-浏览（产品）区域</li><li>Fast Zone-快速（购买）区域</li></ul><h2 id="渠道种类" tabindex="-1"><a class="header-anchor" href="#渠道种类" aria-hidden="true">#</a> 渠道种类</h2><h3 id="kc-主要客户" tabindex="-1"><a class="header-anchor" href="#kc-主要客户" aria-hidden="true">#</a> KC-主要客户</h3><ul><li>活动计划及销售手册</li><li>联合业务和促销计划</li></ul><h3 id="da-直供客户" tabindex="-1"><a class="header-anchor" href="#da-直供客户" aria-hidden="true">#</a> DA-直供客户</h3><ul><li>贸易条款，客户业绩以及促销活动计划</li><li>加强客户服务</li><li>直接控制/计划及卖进</li></ul><h3 id="mt-modern-trade-现代通路" tabindex="-1"><a class="header-anchor" href="#mt-modern-trade-现代通路" aria-hidden="true">#</a> MT-Modern Trade-现代通路</h3><h3 id="gt-general-trade-传统通路" tabindex="-1"><a class="header-anchor" href="#gt-general-trade-传统通路" aria-hidden="true">#</a> GT-General Trade-传统通路</h3><ul><li>投资回报率</li><li>分销覆盖率</li><li>业务增长</li></ul><h3 id="h-hy-hyper-market-大卖场" tabindex="-1"><a class="header-anchor" href="#h-hy-hyper-market-大卖场" aria-hidden="true">#</a> H/HY-Hyper Market-大卖场</h3><h3 id="c-c-cash-carry-量贩店" tabindex="-1"><a class="header-anchor" href="#c-c-cash-carry-量贩店" aria-hidden="true">#</a> C&amp;C-Cash&amp;Carry-量贩店</h3><h3 id="ls-large-super-大超市" tabindex="-1"><a class="header-anchor" href="#ls-large-super-大超市" aria-hidden="true">#</a> LS- Large Super-大超市</h3><h3 id="ss-small-super-小超市" tabindex="-1"><a class="header-anchor" href="#ss-small-super-小超市" aria-hidden="true">#</a> SS-Small Super-小超市</h3><h3 id="cvs-convenient-store-便利店" tabindex="-1"><a class="header-anchor" href="#cvs-convenient-store-便利店" aria-hidden="true">#</a> CVS-Convenient Store-便利店</h3><h3 id="g-k-grocery-kiosk-杂货店" tabindex="-1"><a class="header-anchor" href="#g-k-grocery-kiosk-杂货店" aria-hidden="true">#</a> G/K-Grocery/Kiosk-杂货店</h3><h3 id="counter-售货亭-专柜" tabindex="-1"><a class="header-anchor" href="#counter-售货亭-专柜" aria-hidden="true">#</a> Counter-售货亭/专柜</h3><h3 id="health-drug-保健-药店" tabindex="-1"><a class="header-anchor" href="#health-drug-保健-药店" aria-hidden="true">#</a> Health&amp;Drug-保健/药店</h3><h3 id="mom-pop-夫妻店" tabindex="-1"><a class="header-anchor" href="#mom-pop-夫妻店" aria-hidden="true">#</a> Mom&amp;Pop-夫妻店</h3><h3 id="whs-ws-wholesaler-批发商" tabindex="-1"><a class="header-anchor" href="#whs-ws-wholesaler-批发商" aria-hidden="true">#</a> WHS/WS-Wholesaler-批发商</h3><p><em>XMind - Trial Version</em></p>',127),n=[d];function s(l,t){return e(),r("div",null,n)}const c=a(h,[["render",s],["__file","UL INFO.html.vue"]]);export{c as default};
